java:
int x = 10;

if (x > 0) {
    System.out.println("Positive");
} else {
    System.out.println("Non-positive");
}

python:
x = 10

if x > 0:
    print("Positive")
else:
    print("Non-positive")

java:
int x = 10;

if (x > 0) {
    System.out.println("Positive");
    if (x % 2 == 0) {
        System.out.println("Even");
    } else {
        System.out.println("Odd");
    }
} else {
    System.out.println("Non-positive");
}

python:
x = 10

if x > 0:
    print("Positive")
    if x % 2 == 0:
        print("Even")
    else:
        print("Odd")
else:
    print("Non-positive")

java:
if (b > a) {     
    System.out.println("b is greater than a"); 
} else if (a == b) {
    System.out.println("a and b are equal");
}

python:
if b > a:
    print("b is greater than a")
elif a == b:
    print("a and b are equal")

java:
int day = 3;

switch (day) {
    case 1:
        System.out.println("Monday");
        break;
    case 2:
        System.out.println("Tuesday");
        break;
    // ... (other cases)
    default:
        System.out.println("Unknown day");
}

python:
day = 3

if day == 1:
    print("Monday")
elif day == 2:
    print("Tuesday")
# ... (other cases)
else:
    print("Unknown day")
}
