public class Greetings {
    public static String greet(String name) {
        return "Hello, " + name + "!";
    }
}

public class MathOperations {
    public static int addNumbers(int... numbers) {
        int sum = 0;
        for (int num : numbers) {
            sum += num;
        }
        return sum;
    }
}
